/** @module @category Offline */ import { getOmrsServiceWorker } from "./service-worker.js";
/**
 * Sends the specified message to the application's service worker.
 * @param message The message to be sent.
 * @returns A promise which completes when the message has been successfully processed by the Service Worker.
 */ export async function messageOmrsServiceWorker(message) {
    const sw = await getOmrsServiceWorker();
    return sw ? await sw.messageSW(message) : {
        success: false,
        result: undefined,
        error: 'No service worker has been registered. This is typically the case when the application has been built without offline-related features.'
    };
}
