/** @module @category Extension */ import { useMemo } from "react";
import { useAssignedExtensions } from "./useAssignedExtensions.js";
/**
 * Extract meta data from all extension for a given extension slot.
 * @param extensionSlotName
 */ export function useExtensionSlotMeta(extensionSlotName) {
    const extensions = useAssignedExtensions(extensionSlotName);
    return useMemo(()=>Object.fromEntries(extensions.map((ext)=>[
                ext.name,
                ext.meta
            ])), [
        extensions
    ]);
}
