/** @module @category UI */ import { useEffect, useState } from "react";
function getLayout() {
    let layout = 'tablet';
    document.body.classList.forEach((cls)=>{
        switch(cls){
            case 'omrs-breakpoint-lt-tablet':
                layout = 'phone';
                break;
            case 'omrs-breakpoint-gt-small-desktop':
                layout = 'large-desktop';
                break;
            case 'omrs-breakpoint-gt-tablet':
                layout = 'small-desktop';
                break;
        }
    });
    return layout;
}
export function useLayoutType() {
    const [type, setType] = useState(getLayout);
    useEffect(()=>{
        const handler = ()=>{
            setType(getLayout());
        };
        window.addEventListener('resize', handler);
        return ()=>window.removeEventListener('resize', handler);
    }, []);
    return type;
}
export const isDesktop = (layout)=>layout === 'small-desktop' || layout === 'large-desktop';
